$(document).ready(function ($) {
    $('.shop__cart-quick').on('click', function () {
            var productID = $(this).attr('data-product-id');
            var data = {
                    id: productID,
                    action: 'quick_action',
                    nonce: quick.nonce,
            };
            $.ajax({
                    url: quick.url,
                    data: data,
                    type: 'POST',
                    dataType: 'json',
                    beforeSend: function (xhr) {
                            $('.shop__pop-content').text('Загрузка')
                    },
                    success: function (data) {
                            $('.shop__pop-content').html(data.product);
                            var owl = $('.shop__miniature-big-wrapper');
                            var owl2 = $('.shop__miniature-small-wrapper');
                            owl.trigger('destroy.owl.carousel');
                            owl2.owlCarousel({
                                    items: 4,
                                    margin: 20,
                                    loop: !0,
                                    autoplay: !0,
                                    pagination: !1,
                                    nav: !1,
                                    itemsTablet: [768, 4],
                                    itemsDesktop: [1199, 3],
                                    responsive: {
                                            0: {
                                                    items: 2,
                                                    nav: !1
                                            },
                                            600: {
                                                    margin: 5,
                                                    nav: !1,
                                                    items: 3
                                            },
                                            1000: {
                                                    items: 4
                                            }
                                    }
                            });
                            $('.single_add_to_cart_button').prepend('<i class="icon-shopping-cart"></i>');
                            $('.shop__pop-img.shop__miniature-small-item a').click(function (e) {
                                    if ($('.shop__pop-img.shop__miniature-big-item img').attr('src') !== $(this).attr('href')) {
                                            $('.shop__pop-img.shop__miniature-big-item img').hide().attr('src', $(this).attr('href')).fadeIn(1000)
                                    }
                                    e.preventDefault()
                            });
                            $(".cart .quantity .qty").before('<button class="number-minus" type="button" onclick="this.nextElementSibling.stepDown();this.nextElementSibling.onchange();">-</button>');
                            $(".cart .quantity .qty").after('<button class="number-plus" type="button" onclick="this.previousElementSibling.stepUp();this.previousElementSibling.onchange();">+</button>');
                            $('.shop__pop-content').find('form.variations_form').wc_variation_form();  
                    }
            })
    })
})